import { useState } from 'react';

const useToken = () => {
  const getToken = () => {
    // ...logic to get token from localStorage or other source
  };

  const [token, setToken] = useState(getToken());

  const login = (newToken) => {
    // ...logic to save the newToken to localStorage or other source
    setToken(newToken);
  };

